mapper.select:
SELECT p.paidAt AS paymentDate, m.name AS memberName, m.phone, prod.name AS productName, prod.type AS productType, p.amount, p.method, s.startDate, s.endDate FROM payment p INNER JOIN member m ON p.memberId = m.memberId LEFT JOIN subscription s ON s.paymentId = p.paymentId LEFT JOIN product prod ON p.productId = prod.productId
AND m.name LIKE CONCAT('%', #{keyword}, '%')
AND p.paidAt >= #{startDate}
AND p.paidAt <= #{endDate}
ORDER BY p.paidAt DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM ( SELECT p.paidAt AS paymentDate, m.name AS memberName, m.phone, prod.name AS productName, prod.type AS productType, p.amount, p.method, s.startDate, s.endDate FROM payment p INNER JOIN member m ON p.memberId = m.memberId LEFT JOIN product prod ON p.productId = prod.productId LEFT JOIN subscription s ON s.paymentId = p.paymentId
AND m.name LIKE CONCAT('%', #{keyword}, '%')
AND p.paidAt >= #{startDate}
AND p.paidAt <= #{endDate}
) t

SELECT COALESCE(SUM(amount), 0) AS monthlySales
FROM payment
WHERE FORMATDATETIME(paidAt, 'yyyy-MM') = FORMATDATETIME(CURRENT_DATE, 'yyyy-MM');

SELECT COALESCE(SUM(amount), 0) AS dailySales
FROM payment
WHERE CAST(paidAt AS DATE) = CURRENT_DATE;
