mapper.select:
SELECT top 1 packageId
FROM pt_package
WHERE memberId = #{memberId}

mapper.update:
UPDATE pt_package
SET trainerId = #{trainerId}
WHERE memberId = #{memberId}

UPDATE pt_package
SET paymentId = #{paymentId}
WHERE memberId = #{memberId} AND paymentId IS NULL

mapper.delete:
DELETE FROM pt_package
WHERE memberId = #{memberId}
  AND isActive = FALSE
