mapper.select:
SELECT top 1 packageId
FROM pt_package
WHERE memberId = #{memberId}

mapper.update:
UPDATE pt_package
SET trainerId = #{trainerId}
WHERE memberId = #{memberId}

UPDATE pt_package
SET remainingCount = remainingCount + 1
WHERE packageId = #{packageId}

UPDATE pt_package
SET paymentId = #{paymentId}
WHERE memberId = #{memberId} AND paymentId IS NULL
